INSERT INTO `music_movie` VALUES (594, '心引力', '王俊凯', '国语 ', '2019-11-02', '北京时代峰峻文化艺术发展有限公司 ', 10000, '4', '王俊凯是中国流行男子音乐组合TFBOYS的成员之一，他同时也是一位歌手。王俊凯于2013年出道，通过参加选秀节目《快乐男声》而走红。他和TFBOYS的其他两位成员一起推出了多首热门歌曲，如《青春修炼手册》、《Magic Castle》等。王俊凯的歌唱风格偏向流行舞曲和流行摇滚，他的歌曲常常以阳光、青春和积极向上的主题为主。除了歌唱，王俊凯也参演了一些电视剧和电影，并在娱乐圈中积累了一定的演艺经验。', 100000, 'https://y.qq.com/music/photo_new/T002R800x800M000003qOjYQ2TlNFt_1.jpg?max_age=2592000', 'picture/594.jpg', 'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fimg.lapin365.com%2Fproductpictures%2Fnewcoupons%2Fqqmusic.png&refer=http%3A%2F%2Fimg.lapin365.com&app=2002&size=f9999,10000&q=a80&n=0&g=0n&fmt=auto?sec=1653018676&t=2b364cd82257265f6aacb288251cd464');
